$(document).ready(function(){
    $("div[class='dz-message needsclick']").text("Arraste os documentos para aqui ou clique aqui");
    $("#submit").val("Submeter");

    //Alterar mensagens menu drop-down utilizador
    var HTMLretirado_perfil = $("div.header-nav li.user-link a[href*=ja_profile]").html();
    var novoHTML_perfil = (HTMLretirado_perfil|| "").replace("Profile", "Perfil");
    $("div.header-nav li.user-link a[href*=ja_profile]").html(novoHTML_perfil);

    var HTMLretirado_logout = $("div.header-nav li.user-link a[href*=j_spring_security_logout]").html();
    var novoHTML_logout = (HTMLretirado_logout|| "").replace("Logout", "Sair");
    $("div.header-nav li.user-link a[href*=j_spring_security_logout]").html(novoHTML_logout);

    var HTMLretirado_up_perfil = $("div main ul.breadcrumb").html();
    var novoHTML_up_perfil = (HTMLretirado_up_perfil|| "").replace("Profile", "Perfil");
    $("div main ul.breadcrumb").html(novoHTML_up_perfil);


    //Alterar mensagens User Profile Menu
    $('div.userProfile-body-header').text("Perfil")

    var titulos_detalhesUtilizador = ["Detalhes do Utilizador","Mudar Palavra-Passe","Autentique-se para atualizar o perfil"]
    var i = 0;

    $('div form fieldset legend').each(function(){
      $(this).text(titulos_detalhesUtilizador[i]);
      i++;
    });


    var titulos_detalhesUtilizador2 = ["Nome de Utilizador","Nome","Apelido","Email","Fuso Horário","Palavra-Passe","Confirmar Palavra-Passe"]
    var j = 0;

    $('div form fieldset div.form-row label[for=field1]').each(function(){
      $(this).text(titulos_detalhesUtilizador2[j]);
      j++;
    });

    $('div form fieldset div.form-row label[for=password]').text("Palavra-Passe Atual");

    $('form[id=profile] div.form-errors').text("Falha na autenticação");

    $(function(){
        $("form[id=profile] input.waves-button-input").val("GUARDAR");
    });

});


$(window).on('load', function(){
    $(function(){
        $("form[id=profile] input.waves-button-input").val("GUARDAR");
    });
});
